// src/components/Projects.js
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination, Navigation } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

export default function Projects() {
  const projects = [
    {
      title: "SAE 1 : Python",
      description: "Étude d'une communauté dans un réseau social, avec traitement et analyse de données en Python.",
      link: "https://github.com/toncompte/sae1-python",
    },
    {
      title: "SAE 3.01 : Bibliothèque de jeux",
      description: "Première version d'une application web pour gérer une collection patrimoniale de jeux de société.",
      link: "https://github.com/toncompte/sae3-bibliotheque",
    },
    {
      title: "SAE 4.01 : Amélioration de l'application",
      description: "Audit et refonte de l'application S3, avec amélioration de la base de données et de l'interface.",
      link: "https://github.com/toncompte/sae4-amelioration",
    },
    {
      title: "Projet personnel : Site vitrine BTP",
      description: "Création d'un site vitrine en PHP pour présenter les prestations et collecter les demandes clients.",
      link: "https://github.com/toncompte/site-btp",
    },
  ];

  const skills = [
    "HTML", "CSS", "JavaScript", "PHP", "SQL", "Git", "CakePHP", "Node.js", "TailwindCSS"
  ];

  return (
    <div className="min-h-screen bg-[#111827] text-gray-300">
      {/* Hero */}
      <section className="py-16 flex flex-col items-center justify-center text-center">
        <h1 className="text-5xl md:text-7xl font-bold text-white mb-4">Marewane Fertikh</h1>
        <p className="text-xl md:text-2xl text-gray-400 mb-8">
          Étudiant en informatique, passionné par la data et le développement.
        </p>
        <a
          href="/cv.pdf"
          className="bg-purple-600 hover:bg-purple-500 active:bg-purple-700 text-white font-semibold py-3 px-8 rounded-xl transition-colors duration-200 shadow-md"
        >
          Télécharger mon CV
        </a>
      </section>

      {/* Présentation */}
      <section className="py-16 max-w-3xl mx-auto px-4">
        <h2 className="text-3xl font-bold mb-6 text-purple-400 text-center">Présentation</h2>
        <p className="text-gray-300 text-lg leading-relaxed text-center">
          Je m'appelle <span className="font-semibold text-white">Marewane FERTIKH</span> et je suis actuellement étudiant en 2<sup>e</sup> année de BUT informatique. 
          Je découvre le développement depuis ma première année et je me suis progressivement passionné par cet univers exigeant et stimulant.<br /><br />
          J'ai eu l'opportunité de travailler sur des projets variés, ce qui m'a permis de développer des compétences techniques et méthodologiques, notamment en programmation orientée objet, bases de données, et développement web.<br /><br />
          Ma qualité principale est ma <span className="text-purple-400 font-semibold">curiosité</span>, qui me pousse à apprendre de nouveaux langages, outils et méthodologies.
        </p>
      </section>

      {/* Compétences */}
      <section className="py-16 max-w-3xl mx-auto px-4">
        <h2 className="text-3xl font-bold mb-6 text-purple-400 text-center">Mes compétences</h2>
        <div className="flex flex-wrap justify-center gap-4">
          {skills.map((skill) => (
            <span
              key={skill}
              className="bg-[#232336] text-purple-400 border border-purple-500/30 px-4 py-2 rounded-lg font-semibold text-sm shadow hover:bg-purple-600 hover:text-white transition-colors duration-200"
            >
              {skill}
            </span>
          ))}
        </div>
      </section>

      {/* Projets */}
      <section id="projects" className="py-16 max-w-6xl mx-auto px-4">
        <h2 className="text-3xl font-bold mb-8 text-purple-400 text-center">Mes Projets</h2>
        <Swiper
          modules={[Pagination, Navigation]}
          spaceBetween={30}
          slidesPerView={1}
          breakpoints={{
            640: { slidesPerView: 1 },
            1024: { slidesPerView: 2 },
            1440: { slidesPerView: 3 },
          }}
          navigation
          pagination={{ clickable: true }}
          className="modern-swiper"
        >
          {projects.map((project, index) => (
            <SwiperSlide key={index} className="flex h-full">
              <div className="bg-[#181926] rounded-2xl shadow-2xl px-8 py-10 h-full w-full min-h-[260px] max-h-[340px] flex flex-col justify-between border border-purple-500/20 transition-transform hover:-translate-y-2 hover:shadow-purple-500/30 duration-300">
                <div>
                  <h3 className="text-xl font-semibold mb-2 text-purple-400">{project.title}</h3>
                  <p className="text-gray-300 text-base mb-4">{project.description}</p>
                </div>
                <a
                  href={project.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-block mt-auto bg-purple-600 hover:bg-purple-500 active:bg-purple-700 text-white px-4 py-2 rounded-xl transition shadow-lg font-semibold"
                >
                  Voir le dépôt
                </a>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </section>

      {/* Contact */}
      <section className="py-16 max-w-md mx-auto px-4">
        <h2 className="text-3xl font-bold mb-6 text-purple-400 text-center">Contact</h2>
        <form className="flex flex-col gap-4">
          <input className="rounded-lg px-4 py-2 bg-[#181926] text-gray-300 border border-purple-500/20 focus:outline-none focus:border-purple-500 transition" placeholder="Votre nom" />
          <input className="rounded-lg px-4 py-2 bg-[#181926] text-gray-300 border border-purple-500/20 focus:outline-none focus:border-purple-500 transition" placeholder="Votre email" />
          <input className="rounded-lg px-4 py-2 bg-[#181926] text-gray-300 border border-purple-500/20 focus:outline-none focus:border-purple-500 transition" placeholder="Objet" />
          <textarea className="rounded-lg px-4 py-2 bg-[#181926] text-gray-300 border border-purple-500/20 focus:outline-none focus:border-purple-500 transition" placeholder="Votre message" rows={4} />
          <button
            type="submit"
            className="bg-purple-600 hover:bg-purple-500 active:bg-purple-700 text-white font-semibold py-2 rounded-xl transition-colors duration-200 shadow"
          >
            Envoyer
          </button>
        </form>
      </section>

      {/* Portfolio Code Button */}
      <div className="py-8 flex justify-center">
        <a
          href="https://github.com/toncompte/portfolio-marewane"
          target="_blank"
          rel="noopener noreferrer"
          className="bg-purple-600 hover:bg-purple-500 active:bg-purple-700 text-white font-semibold py-3 px-8 rounded-xl transition-colors duration-200 shadow-md"
        >
          Voir le code du portfolio
        </a>
      </div>

      <style jsx global>{`
        body {
          background: #111827 !important;
        }
        .swiper-button-next,
        .swiper-button-prev {
          width: 28px !important;
          height: 28px !important;
          color: #fff !important;
          background: none !important;
          border: none !important;
          top: 50% !important;
          transform: translateY(-50%);
          box-shadow: none !important;
          transition: color 0.2s;
          z-index: 10;
          right: -36px !important;
          left: -36px !important;
        }
        .swiper-button-next {
          right: -36px !important;
          left: auto !important;
        }
        .swiper-button-prev {
          left: -36px !important;
          right: auto !important;
        }
        .swiper-button-next:after,
        .swiper-button-prev:after {
          font-size: 22px !important;
          font-weight: bold;
        }
        .swiper-button-next:hover,
        .swiper-button-prev:hover {
          color: #a78bfa !important;
        }
        /* Remove background shapes */
        .modern-swiper .swiper-pagination-bullet {
          background: #a78bfa !important;
          opacity: 0.5;
        }
        .modern-swiper .swiper-pagination-bullet-active {
          background: #a78bfa !important;
          opacity: 1;
        }
      `}</style>
    </div>
  );
}
