// src/components/Projects.js
import { useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination, Navigation } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

export default function Projects() {
  const projects = [
    {
      title: "SAE 1 : Python",
      description: "Étude d'une communauté dans un réseau social, avec traitement et analyse de données en Python.",
      link: "https://github.com/toncompte/sae1-python",
      technos: ["Python", "Jupyter"],
    },
    {
      title: "SAE 3.01 : Bibliothèque de jeux",
      description: "Première version d'une application web pour gérer une collection patrimoniale de jeux de société.",
      link: "https://github.com/toncompte/sae3-bibliotheque",
      technos: ["HTML", "CSS", "PHP", "SQL"],
    },
    {
      title: "SAE 4.01 : Amélioration de l'application",
      description: "Audit et refonte de l'application S3, avec amélioration de la base de données et de l'interface.",
      link: "https://github.com/toncompte/sae4-amelioration",
      technos: ["HTML", "CSS", "PHP", "SQL"],
    },
    {
      title: "Projet personnel : Site vitrine BTP",
      description: "Création d'un site vitrine en PHP pour présenter les prestations et collecter les demandes clients.",
      link: "https://github.com/toncompte/site-btp",
      technos: ["PHP", "HTML", "CSS"],
    },
  ];

  // Ajout de classes Tailwind aux éléments Swiper générés
  useEffect(() => {
    setTimeout(() => {
      document.querySelectorAll('.swiper-pagination-bullet').forEach(bullet => {
        bullet.classList.add(
          "bg-purple-400", "opacity-100", "w-4", "h-4", "mx-1", "border-2", "border-white", "transition", "duration-200"
        );
      });
      document.querySelectorAll('.swiper-pagination-bullet-active').forEach(bullet => {
        bullet.classList.add("bg-purple-600", "scale-110", "border-purple-600");
      });
      document.querySelectorAll('.swiper-button-next, .swiper-button-prev').forEach(btn => {
        btn.classList.add(
          "bg-purple-900/70", "text-purple-300", "rounded-full", "w-11", "h-11", "top-1/2", "-translate-y-1/2", "shadow-lg", "transition", "duration-200", "flex", "items-center", "justify-center"
        );
      });
    }, 100);
  });

  return (
    <section id="projects" className="py-16 text-center bg-gradient-to-b from-gray-900 to-gray-800">
      <h2 className="text-3xl font-bold mb-8 text-purple-400">Mes Projets</h2>
      <Swiper
        modules={[Pagination, Navigation]}
        spaceBetween={30}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        breakpoints={{
          640: { slidesPerView: 1 },
          768: { slidesPerView: 2 },
          1024: { slidesPerView: 3 },
        }}
        className="modern-swiper"
      >
        {projects.map((project, index) => (
          <SwiperSlide key={index}>
            <div className="bg-white/10 backdrop-blur-md rounded-2xl shadow-2xl p-8 h-full flex flex-col justify-between border border-white/20 transition-transform hover:-translate-y-2 hover:shadow-purple-500/30 duration-300">
              <div>
                <h3 className="text-xl font-semibold mb-2 text-white">{project.title}</h3>
                <div className="flex flex-wrap justify-center gap-2 mb-3">
                  {project.technos.map((tech, idx) => (
                    <span
                      key={idx}
                      className="bg-purple-600/80 text-xs px-2 py-1 rounded text-white shadow"
                    >
                      {tech}
                    </span>
                  ))}
                </div>
                <p className="text-gray-200 text-sm mb-4">{project.description}</p>
              </div>
              <a
                href={project.link}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mt-auto bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-xl transition shadow-lg font-semibold"
              >
                Voir le dépôt
              </a>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
}
