import React, { useState } from "react";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: ""
  });
  const [status, setStatus] = useState("");

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setStatus("Envoi...");

    const res = await fetch("/api/sendEmail", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData)
    });

    if (res.ok) {
      setStatus("Message envoyé !");
      setFormData({ name: "", email: "", message: "" });
    } else {
      setStatus("Erreur lors de l'envoi.");
    }
  };

  return (
    <section id="contact" className="py-12 px-4 max-w-xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center text-purple-400">Contact</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          className="w-full p-2 rounded text-black"
          type="text"
          name="name"
          placeholder="Votre nom"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          className="w-full p-2 rounded text-black"
          type="email"
          name="email"
          placeholder="Votre email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <textarea
          className="w-full p-2 rounded text-black"
          name="message"
          rows="5"
          placeholder="Votre message"
          value={formData.message}
          onChange={handleChange}
          required
        />
        <button
          type="submit"
          className="bg-purple-500 hover:bg-purple-600 px-4 py-2 rounded text-white"
        >
          Envoyer
        </button>
        {status && <p className="text-center">{status}</p>}
      </form>
    </section>
  );
}
