// src/components/Projects.js
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination, Navigation } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

export default function Projects() {
  const projects = [
    {
      title: "SAE 1 : Python",
      description: "Étude d'une communauté dans un réseau social, avec traitement et analyse de données en Python.",
      link: "https://github.com/toncompte/sae1-python",
    },
    {
      title: "SAE 3.01 : Bibliothèque de jeux",
      description: "Première version d'une application web pour gérer une collection patrimoniale de jeux de société.",
      link: "https://github.com/toncompte/sae3-bibliotheque",
    },
    {
      title: "SAE 4.01 : Amélioration de l'application",
      description: "Audit et refonte de l'application S3, avec amélioration de la base de données et de l'interface.",
      link: "https://github.com/toncompte/sae4-amelioration",
    },
    {
      title: "Projet personnel : Site vitrine BTP",
      description: "Création d'un site vitrine en PHP pour présenter les prestations et collecter les demandes clients.",
      link: "https://github.com/toncompte/site-btp",
    },
  ];

  return (
    <>
      {/* Présentation */}
      <section className="py-16 bg-gray-900">
        <div className="max-w-4xl mx-auto px-4 rounded-2xl shadow-lg bg-gray-900">
          {/* ... contenu de la présentation ... */}
        </div>
      </section>

      {/* Compétences */}
      <section className="py-16 bg-gray-800">
        <div className="max-w-4xl mx-auto px-4 rounded-2xl shadow-lg bg-gray-800">
          {/* ... contenu des compétences ... */}
        </div>
      </section>

      {/* Projets */}
      <section id="projects" className="relative py-16 bg-gray-950">
        <div className="max-w-6xl mx-auto px-6 py-10 rounded-2xl shadow-2xl bg-gray-900 border border-purple-500/20">
          <h2 className="text-3xl font-bold mb-8 text-purple-400 text-center">Mes Projets</h2>
          <Swiper
            modules={[Pagination, Navigation]}
            spaceBetween={30}
            slidesPerView={1}
            navigation
            pagination={{ clickable: true }}
            className="modern-swiper flex items-stretch"
          >
            {projects.map((project, index) => (
              <SwiperSlide key={index} className="h-full flex">
                <div className="bg-white/10 backdrop-blur-md rounded-2xl shadow-2xl px-10 py-12 h-full w-full min-h-[280px] max-h-[380px] flex flex-col justify-between border border-white/20 transition-transform hover:-translate-y-2 hover:shadow-purple-500/30 duration-300">
                  <div>
                    <h3 className="text-xl font-semibold mb-2 text-white">{project.title}</h3>
                    <p className="text-gray-200 text-sm mb-4">{project.description}</p>
                  </div>
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-block mt-auto bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-xl transition shadow-lg font-semibold"
                  >
                    Voir le dépôt
                  </a>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
        <div className="absolute left-1/2 -bottom-4 -translate-x-1/2 w-40 h-2 bg-purple-500/40 rounded-full blur-sm" />
      </section>

      {/* Contact */}
      <section className="py-16 bg-gray-800">
        <div className="max-w-md mx-auto px-4 rounded-2xl shadow-lg bg-gray-100">
          {/* ... formulaire de contact ... */}
        </div>
      </section>
    </>
  );
}
