import { useState } from "react";

export default function Contact() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Envoi en cours...");

    const res = await fetch('/api/sendMail', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });

    if (res.ok) {
      setStatus("Message envoyé !");
      setForm({ name: "", email: "", message: "" });
    } else {
      setStatus("Erreur lors de l'envoi.");
    }
  };

  return (
    <section id="contact" className="min-h-screen flex flex-col items-center justify-center text-center">
      <h2 className="text-3xl font-bold mb-4 text-purple-400">Contact</h2>
      <form onSubmit={handleSubmit} className="space-y-4 max-w-md w-full">
        <input
          type="text"
          name="name"
          placeholder="Votre nom"
          value={form.name}
          onChange={handleChange}
          className="w-full p-2 rounded text-black"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Votre email"
          value={form.email}
          onChange={handleChange}
          className="w-full p-2 rounded text-black"
          required
        />
        <textarea
          name="message"
          placeholder="Votre message"
          value={form.message}
          onChange={handleChange}
          className="w-full p-2 rounded text-black"
          required
        />
        <button type="submit" className="bg-purple-500 px-4 py-2 rounded hover:bg-purple-600">
          Envoyer
        </button>
      </form>
      {status && <p className="mt-2">{status}</p>}
    </section>
  );
}
